import {extractUser} from '../../lib/api-helper'
import nextConnect from 'next-connect'
import middleware from '../../middlewares/middleware'
import cors from 'cors'
import initMiddleware from '../../lib/initMiddleware'

const handler = nextConnect()
//const cors = initMiddleware(Cors({origin:'http://124.93.93.93:3000', methods:['POST']}))
handler.use(middleware);

//console.log(cors({origin:'http://124.93.93.93:3000', methods:['POST']}))
handler.get('/api/users', cors({origin:'http://124.93.93.93:3000', methods:['POST']}), async(req, res)=>res.send("welcome"));


export default handler
