import Head from '../components/head'
import Nav from '../components/nav'
import Intro from '../components/Intro'
import Footer from '../components/Footer'
import {categoryReturn, filterCat} from '../lib/posts'
import React, {useEffect} from 'react'
import fetch from 'node-fetch'
import useSWR from 'swr'
import Link from 'next/link'
import Loader from '../components/Loader'


const fetcher =async (url)=>{const r = await fetch(url)
return r.json()}
const Post =(props)=>{
const {post} = props;
return(                  
    <div className="col-lg-4 col-md-6 col-sm-6">
        <div className="news-container ">
            {
            post.image_link !== null ?
            <Link href='/news/[cat]/[title]' as ={`/news/${post.category}/${post.caption}`}>
              <a className="news-items" >
                <img src={post.image_link} className="img-fluid sub-inner-img" />
              </a>
            </Link>
            
            :
            <Link href='/news/[cat]/[title]' as ={`/news/${post.category}/${post.caption}`}>
              <a className="news-items">
                <img src={post.image_link}  className="img-fluid sub-inner-img"/>
              </a>
            </Link>
            
            }
            <Link href='/news/[cat]/[title]' as={`/news/${post.category}/${post.caption}`}>
              <a className="news-items" >{post.caption}
              </a> 
            </Link>
            
        </div>
    </div>
    )
}

const Abstract =(props)=>{
    const {post} = props;
    return(                  
        <div className="col-md-12 col-sm-12">
            <div className="news-container news-tablet-container">
                {
                post.image_link !== null ?
                <Link href='/news/[cat]/[title]' as ={`/news/${post.category}/${post.caption}`}>
                  <a className="news-items">
                    <img src={post.image_link}  className="img-fluid sub-inner-img"/>
                  </a>
                </Link>
                :
                <Link href='/news/[cat]/[title]' as ={`/news/${post.category}/${post.caption}`}>
                  <a className="news-items">
                    <img src={post.image_link}  className="img-fluid sub-inner-img"/>
                  </a>
                </Link>
                }
              <Link href='/news/[cat]/[title]' as={`/news/${post.category}/${post.caption}`}>
                <a className="news-items" >{post.caption}
                </a> 
              </Link>  
            </div>
        </div>
        )
}

export async function getServerSideProps() {
    //console.log(`${process.env.HOST}/api/posts`)
    const data = await fetcher(`http://localhost:3000/api/posts`)
    return { props: { data } }
  }

 function loading(){
    setTimeout(()=>{
      document.getElementById('loader').style.display ="none"
      document.getElementById('content').style.display="block"
    },2000)
 }
 
function Index (props) {
  useEffect(() => {
    if(props){ loading();}
  }, [])
    const initialData = props.data
   const { data } = useSWR(`http://localhost:3000/api/posts`, fetcher, { initialData })
   const intro = categoryReturn(data.posts, 'intro', 1, 'abstract_cat')[0]
   const business = filterCat(data.posts, {category:'business', headings:'not', abstract_cat:''}).map((post)=><Post key={post._id} post={post}/>)
   const politics = filterCat(data.posts, {category:'politics', headings:'not', abstract_cat:''}).map((post)=><Post key={post._id} post={post}/>)
   const celebrities = filterCat(data.posts, {category:'celebrities', headings:'not', abstract_cat:''}).map((post)=><Post key={post._id} post={post}/>)
   const sport = filterCat(data.posts, {category:'sport', headings:'not', abstract_cat:''}).map((post)=><Post key={post._id} post={post}/>)
   const social_media = filterCat(data.posts, {category:'social_media', headings:'not', abstract_cat:''}).map((post)=><Post key={post._id} post={post}/>)
   const international = filterCat(data.posts, {category:'international', headings:'not', abstract_cat:''}).map((post)=><Post key={post._id} post={post}/>)
   const tech_inno = filterCat(data.posts, {category:'tech_inno', headings:'not', abstract_cat:''}).map((post)=><Post key={post._id} post={post}/>)
   const health = filterCat(data.posts, {category:'health', headings:'not', abstract_cat:''}).map((post)=><Post key={post._id} post={post}/>)
   const latest_news =  categoryReturn(data.posts, 'latest_news', 5, 'abstract_cat').map((post)=><Abstract key={post._id} post={post}/>)
   const top_news =  categoryReturn(data.posts, 'top_stories', 5, 'abstract_cat').map((post)=><Abstract key={post._id} post={post}/>)
    return (
    <div  id="app-container">
        <Loader/>
        <div id="content" style={{display:'none'}} >
        <Head/>
        <Nav/>
        <Intro intro={intro} posts= {{latest_news, top_news, business, politics, celebrities, sport, social_media, international,
        tech_inno, health}}/>
        <Footer />
        </div>
    </div>
    
    
    )
  }
export default Index
