import Head from '../components/head'
import Nav from '../components/nav'
import Intro from '../components/Intro'
import {categoryReturn, } from '../lib/posts'
import fetch from 'node-fetch'
import useSWR from 'swr'


const fetcher =async (url)=>{const r = await fetch(url)
return r.json()}
const Post =(props)=>{
const {post} = props;
return(                  
    <div className="col-lg-4 col-md-6 col-sm-6">
        <div className="news-container ">
            {
            post.image_link !== null ?
            <a className="news-items" href=''>
            <img src={post.image_link} className="img-fluid sub-inner-img" />
            </a>
            :
            <a className="news-items" href=''>
            <img src={post.image_link}  className="img-fluid sub-inner-img"/>
            </a>
            }
          <a className="news-items" href="">{post.caption}
          </a>   
        </div>
    </div>
    )
}

const Abstract =(props)=>{
    const {post} = props;
    return(                  
        <div className="col-md-12 col-sm-12">
            <div className="news-container news-tablet-container">
                {
                post.image_link !== null ?
                <a className="news-items" href=''>
                <img src={post.image_link} className="img-fluid sub-inner-img" />
                </a>
                :
                <a className="news-items" href=''>
                <img src={post.image_link}  className="img-fluid sub-inner-img"/>
                </a>
                }
              <a className="news-items" href="">{post.caption}
              </a>   
            </div>
        </div>
        )
}

export async function getServerSideProps() {
    //console.log(`${process.env.HOST}/api/posts`)
    const data = await fetcher(`http://localhost:3000/api/posts`)
    return { props: { data } }
  }
 
function Index (props) {
    const initialData = props.data
   const { data } = useSWR(`http://localhost:3000/api/posts`, fetcher, { initialData })
   const intro = categoryReturn(data.posts, 'intro', 1, 'abstract_cat')[0]
   const business = filterCat(data, {category:'business', headings:'not', abstract_cat:''}).map((post)=><Post key={post._id} post={post}/>)
   const politics = filterCat(data, {category:'politics', headings:'not', abstract_cat:''}).map((post)=><Post key={post._id} post={post}/>)
   const celebrities = filterCat(data, {category:'celebrities', headings:'not', abstract_cat:''}).map((post)=><Post key={post._id} post={post}/>)
   const sport = filterCat(data, {category:'sport', headings:'not', abstract_cat:''}).map((post)=><Post key={post._id} post={post}/>)
   const social_media = filterCat(data, {category:'social_media', headings:'not', abstract_cat:''}).map((post)=><Post key={post._id} post={post}/>)
   const international = filterCat(data, {category:'international', headings:'not', abstract_cat:''}).map((post)=><Post key={post._id} post={post}/>)
   const tech_inno = filterCat(data, {category:'tech_inno', headings:'not', abstract_cat:''}).map((post)=><Post key={post._id} post={post}/>)
   const health = filterCat(data, {category:'health', headings:'not', abstract_cat:''}).map((post)=><Post key={post._id} post={post}/>)
   const latest_news =  categoryReturn(data.posts, 'latest_news', 5, 'abstract_cat').map((post)=><Abstract key={post._id} post={post}/>)
   const top_news =  categoryReturn(data.posts, 'top_stories', 5, 'abstract_cat').map((post)=><Abstract key={post._id} post={post}/>)
    return (
    <div>
        <Head/>
        <Nav/>
        <Intro intro={intro} posts= {{latest_news, top_news, business, politics, celebrities, sport, social_media, international,
        tech_inno, health}}/>
    </div>
    
    )
  }
export default Index
