//initialize the default app
var admin = require('firebase-admin');
var meco = require('meco.json');

admin.initializeApp({credential:admin.credential.cert(meco),
databaseURL:'https://meco-7dd4b.firebaseio.com'});

async function database(req, res, next) {
  if (!admin.isConnected()) await admin.connect();
  req.dbClient = admin;
  req.db = admin.firestore();
  return next();
}

export default database;



