import {useCurrentUser} from '../lib/hooks.js'
import nextConnect from 'next-connect'
import React, {useState, useEffect} from 'react'
import Head from '../components/head'

import Router from 'next/router'
import Link from 'next/link'

const Signup= ()=>{
const [errorMsg, setErrorMsg] = useState('')
const [user, {mutate}] = useCurrentUser()
useEffect(()=>{
    if(user){
        Router.replace('/')
    }
}, [user])

const handleSubmit = async (e)=>{
    e.preventDefault();
    const body = {
        name: e.currentTarget.name.value,
        email:e.currentTarget.email.value,
        password:e.currentTarget.password.value
    }
    const res = await fetch('api/users/',{
        method: 'POST',
        headers:{'Content-Type':'application/json'},
        body:JSON.stringify(body),
    });

    if(res.status===201){
        const userObj = await res.json();
        mutate(userObj);
    }
    else{
        setErrorMsg(await res.text());
    }

}


    return(
    <div>
        <Head/>
    <div id="main-container" className="main-container">
    <div className="content-container">
     <div id="site-details">
        <a href="{{route('welcome')}}" style={{fontSize: "2rem"}}>Factonews</a>
      </div>
      <div id="page-title">
        Create Account
      </div>
        <div id="page-contents" >
        <form id="register" method="post" action="{{route('register')}}" >
          <label className="input-container">
            <span id="fname-tag" className="input-label email-label">First name</span>
          <input  id="fname" type="text" className="" name="fname" value="" autofocus/>
          </label> 

          <label className="input-container">
            <span id="lname-tag" className="input-label email-label">Last name</span>
          <input  id="lname" type="text" className="" name="lname" value=""/>
          </label>

          <label className="input-container">
            <span id="email-tag" className="input-label email-label">Email</span>
          <input  id="email" type="email" className="" name="email" value="" />
          
          </label> 

          <label className="input-container">
            <span id="password-tag" className="input-label password-label">Password</span>
          <input id="password" type="password" name="password" value=""/>
         
          </label>

          <label className="input-container">
            <span id="confirm_password-tag" className="input-label password-label">Confirm password</span>
          <input id="password_confirmation" type="password" name="password_confirmation" value="" />
         
          </label>


          <button id="register-button" type="submit" className="submit-button">
            {'Register'}
          </button>


          <div className="forgot-section">
            <a href="/" >Forgot password ?</a>
            <a href="" className="signup">Already have an Account?</a>
          </div>
        </form>
        </div>
        </div>
        </div>


    </div>        
    )
}

export default Signup;
