import Head from '../components/head'
import Nav from '../components/nav'
import Intro from '../components/Intro'
import fetch from 'node-fetch'
import useSWR from 'swr'


const fetcher =(url)=>{return fetch(url).then(r => r.json())}
const Post =(props)=>{
    const {post} = props;
    //console.log(`host : ${process.env.HOST}/api/posts`)
return(                  
    <div className="col-lg-4 col-md-6 col-sm-6">
        <div className="news-container">
            {
            post.image_link !== null ?
            <a className="news-items" href=''>
            <img src='/images/president-buhari-e1567317241105-375x195.jpg' className="img-fluid sub-inner-img" />
            </a>
            :
            <a className="news-items" href=''>
            <img src='/images/president-buhari-e1567317241105-375x195.jpg'  className="img-fluid sub-inner-img"/>
            </a>
            }
          <a className="news-items" href="">{post.caption}
          </a>   
        </div>
    </div>
    )
}
export async function getServerSideProps() {
    //console.log(`${process.env.HOST}/api/posts`)
    const data = await fetcher(`http://localhost:3000/api/posts`)
    return { props: { data } }
  }
const categoryReturn =(data, category, num)=>{
    //console.log(data)
   return data.reduce((acc, post)=>{
        if(acc.length === num) return acc;
        if(post.category === category) acc.push(post);
        return acc;
    },[])
    
}  
function Index (props) {
    const initialData = props.data
   const { data } = useSWR(`http://localhost:3000/api/posts`, fetcher, { initialData })
   const business =  categoryReturn(data.posts, 'business', 6).map((post)=><Post key={post._id} post={post}/>)
   const politics =  categoryReturn(data.posts, 'politics', 6).map((post)=><Post key={post._id} post={post}/>)
   const celebrities =  categoryReturn(data.posts, 'celebrities', 6).map((post)=><Post key={post._id} post={post}/>)
   const sport =  categoryReturn(data.posts, 'sport', 6).map((post)=><Post key={post._id} post={post}/>)
   const social_media =  categoryReturn(data.posts, 'social_media', 6).map((post)=><Post key={post._id} post={post}/>)
   const internation =  categoryReturn(data.posts, 'internation', 6).map((post)=><Post key={post._id} post={post}/>)
   const tech_inno =  categoryReturn(data.posts, 'tech_inno', 6).map((post)=><Post key={post._id} post={post}/>)
   const health =  categoryReturn(data.posts, 'health', 6).map((post)=><Post key={post._id} post={post}/>)
    return (
    <div>
        <Head/>
        <Nav/>
        <Intro posts= {{business, politics, celebrities, sport, social_media, internation,
        tech_inno, health}}/>
    </div>
    
    )
  }


/*const Index= () => {
    return(
    <div>
        <Head/>
        <Nav/>
    </div>
    )
}*/
export default Index
