import nextConnect from 'next-connect';
import isEmail from 'validator/lib/isEmail';
import normalizeEmail from 'validator/lib/normalizeEmail';
import bcrypt from 'bcryptjs';
import { nanoid } from 'nanoid';
import middleware from '../../middlewares/middleware';
import { extractUser } from '../../lib/api-helper';
import cors from 'cors'
import next, { NextApiRequest, NextApiResponse } from 'next';

const handler = nextConnect();

var whitelist = ['http://example1.com', 'http://example2.com']
var corsOptionsDelegate = function(req, callback) {
  var corsOptions;
  if (whitelist.indexOf(req) !== -1) {
    corsOptions = { origin: true } // reflect (enable) the requested origin in the CORS response
  } else {
    corsOptions = { origin: false } // disable CORS for this request
  }
  callback(null, corsOptions) // callback expects two parameters: error and options
}


handler.use(middleware);


handler.get('/api/users', (req, res)=>{
     if(req.origin) res.send('req.origin')
     res.send("an error occurred")
}, async(req, res)=>res.send("not working"));

handler.post(async (req, res)=>{

    const email = normalizeEmail(req.body.email)
    console.log(req.body.email)
    const {name, password} = req.body;

    if(!name){
        res.status(400).send("Name is  required")
        return;
    }
    if(!isEmail(email)){
        res.status(400).send("invalid email")
        return;
    }
    if(!password){
        res.status(400).send("Password is required")
        return;
    }

    if((await req.db.collection('users').countDocuments({email})>0)){
        res.status(400).send("Email already exist")
    }
    const hashedPassword = await bcrypt.hash(password,10);
    const user = await req.db.collection('users').insertOne(
        {
            _id:nanoid(12),
            name,
            email,
            password:hashedPassword,
            isVerified:false,
        }
    ).then(({ops})=>ops[0]);
    req.logIn(user, (err)=>{
        if(err) throw err;
        res.status(201).json({
            user:extractUser(req)
        })
    })
})

export default handler;
