import Head from '../../components/head'
import Nav from '../../components/nav'
import {categoryReturn, filterCat} from '../../lib/posts'
import { getPosts } from '../../lib/db';
import middleware from '../../middlewares/middleware';


const Post =(props)=>{
  const {post} = props;
  return(                  
      <div className="col-lg-4 col-md-6 col-sm-6">
          <div className="news-container">
              {
              post.image_link !== null ?
              <a className="news-items" href=''>
              <img src={post.image_link} className="img-fluid sub-inner-img" />
              </a>
              :
              <a className="news-items" href=''>
              <img src={post.image_link}  className="img-fluid sub-inner-img"/>
              </a>
              }
            <a className="news-items" href="">{post.caption}
            </a>   
          </div>
      </div>
      )
  }
  
  const Abstract =(props)=>{
      const {post} = props;
      return(                  
          <div className="col-md-12 col-sm-12">
              <div className="news-container">
                  {
                  post.image_link !== null ?
                  <a className="news-items" href=''>
                  <img src={post.image_link} className="img-fluid sub-inner-img" />
                  </a>
                  :
                  <a className="news-items" href=''>
                  <img src={post.image_link}  className="img-fluid sub-inner-img"/>
                  </a>
                  }
                <a className="news-items" href="">{post.caption}
                </a>   
              </div>
          </div>
          )
  }

export async function getServerSideProps(context) {
  await middleware.apply(context.req, context.res);
 const posts = await getPosts(context.req, context.params.cat);
  if (!posts) context.res.statusCode = 404;
  const intro = categoryReturn(posts.posts, context.params.cat, 1, 'headings')[0];
  const currentCat = filterCat(posts.posts, 'category', 'headings', 'abstract_cat', context.params.cat, 'not', '')
  
  return {props:{posts, currentCat, intro}}
}

function Cat(props){
  const {posts} = props.posts
  //console.log(!posts instanceof Array)
//const latest_news =  categoryReturn(posts, 'latest_news', 4, 'abstract_cat').map((post)=><Abstract key={post._id} post={post}/>)

const top_news =  categoryReturn(posts, 'top_stories', 4, 'abstract_cat').map((post)=><Abstract key={post._id} post={post}/>)

const cat = props.currentCat.map((post)=><Post key={post._id} post={post}/>)
//console.log(props.intro instanceof Array)
  return(
    <div>
      <Head/>
        <Nav/>
        <Cat intro={props.intro} posts= {{cat,  top_news}}/>
    </div>
  )
}

