import nextConnect from 'next-connect';
import { nanoid } from 'nanoid';
import middleware from '../../../middlewares/middleware';
import cors from 'cors'

const handler = nextConnect();





handler.use(cors({methods:'POST'})).use(middleware);
console.log('hit posts')
handler.get(async (req, res) => {

  const posts = await req.db
    .collection('posts')
    .find({})
    .sort({createdAt:-1})
    .toArray();
    //console.log(req.headers)
  res.send({posts})
});

handler.post(async (req, res) => {

  if (!req.user) {
    return res.status(401).send('unauthenticated');
  }

  const { image_link, caption, author, source, source_link, 
    category, headings, abstract_cat, content} = req.body;

  if (!content) return res.status(400).send('You must write something');

  const post = {
    _id: nanoid(),
    image_link, 
    caption, 
    author, 
    source, 
    source_link, 
    category, 
    headings, 
    abstract_cat, 
    content,
    createdAt: new Date(),
    creatorId: req.user._id,
    updatedAt:''
  };

  await req.db.collection('posts').insertOne(post);
  return res.send(post);
});

handler.put( async(req, res)=>{

  const {post_id, image_link, caption, author, source, source_link, 
    category, headings, abstract_cat, content} = req.body;

    const myQuery = {_id:post_id};
    const updateValues = {$set:{
    image_link, 
    caption, 
    author, 
    source, 
    source_link, 
    category, 
    headings, 
    abstract_cat, 
    content,
    updatedAt:new Date(),
    }};

    await req.db.collection("posts").updateOne(myQuery, updateValues, function(err, res) {
      if (err) throw err;

    });
  res.send('welcome update')
})

handler.delete(async(req, res)=>{
  const {post_id} = req.body;
console.log(req.body);
