import Head from '../../components/Head'
import React, {useState, useEffect} from 'react'
import { useCurrentUser } from '../../lib/hooks';
import Loader from '../../components/Loader'

import NotFound from '../404'


const CreatePost=()=>{
  const [errMsg, setErrMsg] = useState('')
  const [inputValue, setInputValue] = useState({image_link:'',caption:'', author:'',source:'', source_link:'', category:'', headings:''})
  const [user] = useCurrentUser();
  const [loading, setLoading] = useState(true)
useEffect(() => {
  console.log('something : ', user)
    if(user) {
      setTimeout(()=>{setLoading(false)}, 2000)
      console.log(loading)
    }
  }, [])

  const handleInput = (e)=>{
    const value = e.target.value;
    setInputValue({...inputValue, [e.target.name]:value})  
}

  async function handleSubmit(e){
    e.preventDefault();
    const body = {
      image_link: e.currentTarget.image_link.value,
      caption: e.currentTarget.caption.value,
      author: e.currentTarget.author.value,
      source: e.currentTarget.source.value,
      source_link: e.currentTarget.source_link.value,
      category: e.currentTarget.category.value,
      headings: e.currentTarget.headings.value,
      abstract_cat: e.currentTarget.abstract_cat.value,
      content: e.currentTarget.content.value,
      date: new Date()
    };
   try{
     const res = await fetch(`/api/posts`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(body),
    });
    if(res.status==200||res.status==201){setErrMsg("posted  successfully")}
  }
  catch(err){
    setErrMsg("An error occured, pls check your internet connection and try again")
  }



    // revalidate the `post-pages` key in usePostPages
    //revalidate();
    // Perhaps show a dialog box informing the post has been posted

  }

  return(
    <>
    <Head/>
  {loading ?
    <Loader/> :
     <NotFound/>

   }
    </>
    )
}

export default CreatePost
