import nextConnect from 'next-connect';
import { nanoid } from 'nanoid';
import middleware from '../../../middlewares/middleware';
import cors from 'cors'

const handler = nextConnect();

var whitelist = ['http://localhost:3000']
var corsOptions = {
  origin: function (origin, callback) {
    if (whitelist.indexOf(origin) !== -1) {
      callback(null, true)
    } else {
      callback(new Error('UnAuthorized'))
    }
  }
}

handler.use(cors(corsOptions)).use(middleware);

handler.get(async (req, res) => {

  const posts = await req.db
    .collection('posts')
    .find({})
    .sort({createdAt:-1})
    .toArray();

  res.send({posts})
});

handler.post(async (req, res) => {
    
  if (!req.user) {
    return res.status(401).send('unauthenticated');
  }

  const { image_link, caption, author, source, source_link, 
    category, headings, abstract_cat, content} = req.body;

  if (!content) return res.status(400).send('You must write something');

  const post = {
    _id: nanoid(),
    image_link, 
    caption, 
    author, 
    source, 
    source_link, 
    category, 
    headings, 
    abstract_cat, 
    content,
    createdAt: new Date(),
    creatorId: req.user._id,
  };

  await req.db.collection('posts').insertOne(post);
  return res.send(post);
});

export default handler;
