import Head from '../../components/Head'
import Header from 'next/head'
import Nav from '../../components/Nav'
import Footer from '../../components/Footer'
import React, {useState,useEffect} from 'react'
import fetch from 'node-fetch'
import Link from 'next/link'
import Loader from '../../components/Loader'





const All =(props)=>{
    const {post} = props;
    return(                  
       <>
            <tr className='table-light'>
                <td>{post.caption}</td>
                
                <td><form onSubmit={props.submit} >
                <input type="hidden" id="post_id" value={post._id} name="post_id" />
                <button className="btn btn-success" type="submit" >delete</button>
                </form>
                </td>
            </tr>
       </>
        )
}
const fetcher =async (url)=>{const r = await fetch(url)
    return r.json()}

export async function getServerSideProps() {
    
    const data = await fetcher(`http://localhost:3000/api/posts`)
    return { props: { data } }
  }


 
function Index (props) {
   const {data} = props
   const [msg, setMsg] = useState('')
    const submitHandler = async(e)=>{
        e.preventDefault();
        console.log('something')
        const body = {post_id : e.currentTarget.post_id.value}
        const res = await fetcher(`http://localhost:3000/api/posts/all`, {
         method: 'POST',
         headers:{'Content-Type':'application/json'}, 
     });
    }
   const allObj = data.posts.map((post)=><All key={post._id} post={post} submit={submitHandler}/>)
   
   

    return (  
        <div>
            <Header>
                <link rel="stylesheet" type="text/css" href="/lib/bootstrap/css/bootstrap.min.css"/>
                <link href="/css/all.css" rel="stylesheet"/>
            </Header>
           

            <div className="container">
                <h1>All Posts</h1>
                
                <div className="table-responsive">
                    <table className="table table-bordered">
                        <thead>
                            <tr>
                            <th>Captions</th>
                            <th>Edit</th>
                            <th>delete</th>
                            </tr>
                        </thead>
                        <tbody>
                        {allObj}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    
    )
  }
export default Index
