import Head from '../../components/Head'
import Header from 'next/head'
import Nav from '../../components/Nav'
import Footer from '../../components/Footer'
import React, {useState,useEffect} from 'react'
import fetch from 'node-fetch'
import Link from 'next/link'
import Loader from '../../components/Loader'



const fetcher =async (url)=>{const r = await fetch(url)
return r.json()}



const All =(props)=>{
    const {post} = props;
    return(                  
       <>
            <tr className='table-light'><td>{post.caption}</td></tr>
       </>
        )
}

export async function getServerSideProps() {
    console.log(`${process.env.HOST}/api/posts`)
    const data = await fetcher(`http://localhost:3000/api/posts`)
   
    return { props: { data } }
  }

 
 
function Index (props) {
   const {data} = props
   const allObj = data.posts.map((post)=><All key={post._id} post={post}/>)
   

    return (  
        <div>
            <Header>
                <link href="/css/all.css" rel="stylesheet"/>
            </Header>
            <div>
                <h1>All Posts</h1>
                <div className="container">
                    <div className="row">
                        <div className="col-m4">
                            <div className="card bg-success">
                                <div className="card-body">
                                   
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    
    )
  }
export default Index
