import nextConnect from 'next-connect';
import { nanoid } from 'nanoid';
import middleware from '../../../middlewares/middleware';

const handler = nextConnect();

handler.use(middleware);

handler.get(async (req, res) => {
  const rand = (min, max)=>{
    return String.fromCharCode(Math.floor(Math.random() * (max - min + 1) ) + min , Math.floor(Math.random() * (max - min + 1) ) + min , Math.floor(Math.random() * (max - min + 1) ) + min , Math.floor(Math.random() * (max - min + 1) ) + min , Math.floor(Math.random() * (max - min + 1) ) + min );
  }
  
  const posts = await req.db
    .collection('posts')
    .find({})
    .sort({createdAt:-1})
    .toArray();
  posts.map(value=>{
    req.db.collection('posts').updateOne({id:value._id}, {$set:{caption:'BOKO HARAM: UN, Amnesty International, others react to killing of aid workers' + rand(97, 122) }},
  (err,res)=>{if(err) throw err; console.log(res.result.nModified + "documents updated")})
  })
  res.send({posts})
});

handler.post(async (req, res) => {
  if (!req.user) {
    return res.status(401).send('unauthenticated');
  }

  const { image_link, caption, author, source, source_link, 
    category, headings, abstract_cat, content} = req.body;

  if (!content) return res.status(400).send('You must write something');

  const post = {
    _id: nanoid(),
    image_link, 
    caption, 
    author, 
    source, 
    source_link, 
    category, 
    headings, 
    abstract_cat, 
    content,
    createdAt: new Date(),
    creatorId: req.user._id,
  };

  await req.db.collection('posts').insertOne(post);
  return res.send(post);
});

export default handler;
