import Head from '../../components/Head'
import Nav from '../../components/Nav'
import Footer from '../../components/Footer'
import React, {useState,useEffect} from 'react'
import fetch from 'node-fetch'
import Link from 'next/link'
import Loader from '../../components/Loader'



const fetcher =async (url)=>{const r = await fetch(url)
return r.json()}



const All =(props)=>{
    const {post} = props;
    return(                  
       <>
            <tr><td>{post.caption}</td></tr>
       </>
        )
}

export async function getServerSideProps() {
    console.log(`${process.env.HOST}/api/posts`)
    const data = await fetcher(`http://localhost:3000/api/posts`)
   
    return { props: { data } }
  }

 
 
function Index (props) {
   const {data} = props
   const allObj = data.posts.map((post)=><All key={post._id} post={post}/>)


    return (
      <div>
      <Head/>
        <div className="table-responsive">
            <table className="table table-bordered">
            <thead className="thead-dark">
                <tr>
                <th>Caption</th>
                </tr>
            </thead>
            <tbody>
               {allObj} 
            </tbody>
            </table>
        </div>
        <Footer/>
      </div>
    )
  }
export default Index
