export const categoryReturn =(data, category, num, type)=>{
    return data.reduce((acc, post)=>{
         if(acc.length === num) return acc;
         if(post[type] === category) acc.push(post);
         return acc;
 },[])  
 }

 export const filterCat =(data,attri1,attri2,attri3,opt1, opt2,opt3)=>{
     return data.filter(value=>
         value[attri1] === opt1 && value[attri2]===opt2 &&value[attri3] === opt3
     )
 }

 filterCat(data, query, 'headings', 'abstract_cat', query, 'not', '')
