import Head from '../components/head'
import Nav from '../components/nav'
import fetch from 'node-fetch'
import useSWR from 'swr'


const fetcher =(url)=>{return fetch(url).then(r => r.json())}
const Post =(props)=>{
    const {post} = props;
    //console.log(`host : ${process.env.HOST}/api/posts`)
return(
<section id="main-content" className="animate-bottom section-top-space nav-clear">
    <div className="container-fluid">
        <div className="row">
            <div className="col-md-9">
                <div className="row">
                    <div className="col-md-12 intro-section-container">
                            <div className="intro-img-container" >
                                <img src="" className="img-fluid"/>
                                <div className="intro-text-container">
                                    <a href="/" ></a>    
                                </div>
                            </div>
                    </div>
                </div>
                <div className="container">
                    <div className="row sub-section-row">
                    <div className="center sub-section-title"><a href="/news/celebrities">Celebrities</a></div>
                                       
                    <div className="col-lg-4 col-md-6 col-sm-6">
                        <div className="news-container">
                            {
                            post.image_link !== null ?
                            <a className="news-items" href=''>
                               <img src='/images/president-buhari-e1567317241105-375x195.jpg' className="img-fluid sub-inner-img" />
                           </a>
                           :
                            <a className="news-items" href=''>
                               <img src='/images/president-buhari-e1567317241105-375x195.jpg'  className="img-fluid sub-inner-img"/>
                           </a>
                            }
                      		<a className="news-items" href="">{post.caption}
                      		</a>
                            
                              
                        </div>
                    </div>
                   
                </div>
            </div>
            </div>//end of col-9
            <div className="col-md-3 ">
            
            </div>
        </div>//row end
    </div>//container end
</section>
    )
}
export async function getServerSideProps() {
    //console.log(`${process.env.HOST}/api/posts`)
    const data = await fetcher(`http://localhost:3000/api/posts`)
    return { props: { data } }
  }
const categoryReturn =(data, category, num)=>{
    //console.log(data)
   return data.reduce((acc, post)=>{
        if(acc.length === num) return acc;
        if(post.category === category) acc.push(post);
        return acc;
    },[])
    
}  
function Index (props) {
    const initialData = props.data
   const { data } = useSWR(`http://localhost:3000/api/posts`, fetcher, { initialData })
   const readyData =  categoryReturn(data.posts, 'business', 6).map((post)=><Post key={post._id} post={post}/>)

    return (
    <div>
        <Head/>
        <Nav/>
        {readyData}
    </div>
    
    )
  }


/*const Index= () => {
