import Head from '../components/head'
import Nav from '../components/nav'
import Intro from '../components/Intro'
import fetch from 'node-fetch'
import useSWR from 'swr'


const fetcher =(url)=>{return fetch(url).then(r => r.json())}
const Post =(props)=>{
const {post} = props;
return(                  
    <div className="col-lg-4 col-md-6 col-sm-6">
        <div className="news-container">
            {
            post.image_link !== null ?
            <a className="news-items" href=''>
            <img src={post.image_link} className="img-fluid sub-inner-img" />
            </a>
            :
            <a className="news-items" href=''>
            <img src={post.image_link}  className="img-fluid sub-inner-img"/>
            </a>
            }
          <a className="news-items" href="">{post.caption}
          </a>   
        </div>
    </div>
    )
}

const Abstract =(props)=>{
    const {post} = props;
    return(                  
        <div className="col-md-12 col-sm-12">
            <div className="news-container">
                {
                post.image_link !== null ?
                <a className="news-items" href=''>
                <img src={post.image_link} className="img-fluid sub-inner-img" />
                </a>
                :
                <a className="news-items" href=''>
                <img src={post.image_link}  className="img-fluid sub-inner-img"/>
                </a>
                }
              <a className="news-items" href="">{post.caption}
              </a>   
            </div>
        </div>
        )
}

export async function getServerSideProps() {
    //console.log(`${process.env.HOST}/api/posts`)
    const data = await fetcher(`http://localhost:3000/api/posts`)
    return { props: { data } }
  }
const categoryReturn =(data, category, num, type)=>{
   return data.reduce((acc, post)=>{
        if(acc.length === num) return acc;
        if(post[type] === category) acc.push(post);
        return acc;
    },[])
    
}  
function Index (props) {
    const initialData = props.data
   const { data } = useSWR(`http://localhost:3000/api/posts`, fetcher, { initialData })
   const intro = categoryReturn(data.posts, 'intro', 1, 'abstract_cat')[0]
   const business =  categoryReturn(data.posts, 'business', 6, 'category').map((post)=><Post key={post._id} post={post}/>)
   const politics =  categoryReturn(data.posts, 'politics', 6, 'category').map((post)=><Post key={post._id} post={post}/>)
   const celebrities =  categoryReturn(data.posts, 'celebrities', 6, 'category').map((post)=><Post key={post._id} post={post}/>)
   const sport =  categoryReturn(data.posts, 'sport', 6, 'category').map((post)=><Post key={post._id} post={post}/>)
   const social_media =  categoryReturn(data.posts, 'social_media', 6, 'category').map((post)=><Post key={post._id} post={post}/>)
   const international =  categoryReturn(data.posts, 'international', 6, 'category').map((post)=><Post key={post._id} post={post}/>)
   const tech_inno =  categoryReturn(data.posts, 'tech_inno', 6, 'category').map((post)=><Post key={post._id} post={post}/>)
   const health =  categoryReturn(data.posts, 'health', 6, 'category').map((post)=><Post key={post._id} post={post}/>)
   const latest_news =  categoryReturn(data.posts, 'latest_news', 5, 'abstract_cat').map((post)=><Abstract key={post._id} post={post}/>)
   const top_news =  categoryReturn(data.posts, 'top_stories', 5, 'abstract_cat').map((post)=><Abstract key={post._id} post={post}/>)
    return (
    <div>
        <Head/>
        <Nav/>
        <Intro intro={intro} posts= {{latest_news, top_news, business, politics, celebrities, sport, social_media, international,
        tech_inno, health}}/>
    </div>
    
    )
  }


/*const Index= () => {
    return(
    <div>
        <Head/>
        <Nav/>
    </div>
    )
}*/
export default Index
