import Head from '../../components/Head'
import Header from 'next/head'
import Nav from '../../components/Nav'
import Footer from '../../components/Footer'
import React, {useState,useEffect} from 'react'
import fetching from 'node-fetch'
import Link from 'next/link'
import Loader from '../../components/Loader'





const All =(props)=>{
    const {post} = props;
    return(                  
       
            <tr className='table-light'>
                <td>{post.caption}</td>
                <td><form onSubmit={props.submit} >
                <input type="hidden" id="post_id" value={post._id} name="post_id" />
                <button className="btn btn-success" type="submit" >Edit</button>
                </form>
                </td>
                <td><form onSubmit={props.submit} >
                <input type="hidden" id="post_id" value={post._id} name="post_id" />
                <button className="btn btn-success" type="submit" >delete</button>
                </form>
                </td>
            </tr>
       
        )
}
const fetcher =async (url)=>{const r = await fetching(url)
    return r.json()}

export async function getServerSideProps() {
    
    const data = await fetcher(`http://localhost:3000/api/posts`)
    return { props: { data } }
  }


 
function Index (props) {
   const {data} = props
   const [status, setStatus] = useState(false)
   const [msg, setMsg] = useState('')
   useEffect(()=>{
       console.log(status)
    if(status) setMsg("one document deleted");
    },[msg])
    const submitHandler = async(e)=>{
        e.preventDefault();
        console.log('something')
        const body = {post_id : e.currentTarget.post_id.value}
        const res = await fetch(`http://localhost:3000/api/posts/all`, {
         method: 'POST',
         headers:{'Content-Type':'application/json'}, 
         body:JSON.stringify(body)
     });
     if(res.status ===200){
         setStatus(true)
     }
    }
    
   const allObj = data.posts.map((post)=><All key={post._id} post={post} submit={submitHandler}/>)
   
   

    return (  
        <div>
            <Header>
                <link rel="stylesheet" type="text/css" href="/lib/bootstrap/css/bootstrap.min.css"/>
                <link href="/css/all.css" rel="stylesheet"/>
            </Header>
           

            <div className="container">
                <h1>All Posts</h1>
                {msg &&
                    <div className="alert alert-danger alert-dismissible fade show">
                    <button type="button" className="close" data-dismiss="alert">&times;</button>
                    <strong>{msg}</strong>
                </div>
                }
                <div className="table-responsive">
                    <table className="table table-bordered">
                        <thead>
                            <tr>
                            <th>Captions</th>
                            <th>Edit</th>
                            <th>delete</th>
                            </tr>
                        </thead>
                        <tbody>
                        {allObj}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    
    )
  }
export default Index
