				var temT = style.getPropertyValue('top');
                var temL = style.getPropertyValue('left');
                [blank.style.left, id.style.left] = [style.getPropertyValue('left'), 
                window.getComputedStyle(blank).getPropertyValue('left')]
                [blank.style.top, id.style.top] = [style.getPropertyValue('top'),
                 window.getComputedStyle(blank).getPropertyValue('top')]
                 console.log(window.getComputedStyle(blank).getPropertyValue('top'))
                /*console.log(temL, temT);
                current.style.top = window.getComputedStyle(blank).getPropertyValue('top');
                current.style.left = window.getComputedStyle(blank).getPropertyValue('left');
                */

                    /*$(this).css({'top': $('.blank').css('top'), 'left': $('.blank').css('left'), 'transition':'all 0.5s'});
				$('.blank').css({'top': temT, 'left':temL, 'transition':'all 0.5s'});
				$('.moves').text("moves: " + count++);*/
			}
			else{
				console.log("working")
				alert("Not MOVEABLE");
            }
        }
    } 
    return(
    <div>
        <Head>
            <link rel="stylesheet" type="text/css" href="/css/puzzlestyle.css"/>
        </Head>
        <div className="container">
            <div className="title-container">
                <h2 className="title">Puzzle Game</h2>
                <h2 className="moves"></h2>
            </div>
            <div className="game-container">
            <div id="one" className="numbers one" onClick={onClickHandler}>1</div>
            <div id="two" className="numbers two" onClick={onClickHandler}>2</div>
            <div id="three" className="numbers three" onClick={onClickHandler}>3</div>
            <div id="four" className="numbers four" onClick={onClickHandler}>4</div>
            <div id="five" className="numbers five" onClick={onClickHandler}>5</div>
            <div id="six" className="numbers six" onClick={onClickHandler}>6</div>
            <div id="seven" className="numbers seven" onClick={onClickHandler}>7</div>
            <div id="eight" className="numbers eight" onClick={onClickHandler}>8</div>
            <div id="blank" className="numbers blank blankBgColor" onClick={onClickHandler}></div>
            </div>
        </div>
    </div>
    
    )

}
export default  Puzzle;
