import Head from '../../components/Head'
import Header from 'next/head'
import Nav from '../../components/Nav'
import Footer from '../../components/Footer'
import React, {useState,useEffect} from 'react'
import fetch from 'node-fetch'
import Link from 'next/link'
import Loader from '../../components/Loader'



const fetcher =async (url)=>{const r = await fetch(url)
return r.json()}

const clickHandler = (e)=>{
  
}

const All =(props)=>{
    const {post} = props;
    return(                  
       <>
            <tr className='table-light'>
                <td>{post.caption}</td>
                <td>{post.link}</td>
                <td><button onClick={clickHandler}></button></td>
            </tr>
       </>
        )
}

export async function getServerSideProps() {
    console.log(`${process.env.HOST}/api/posts`)
    const data = await fetcher(`http://localhost:3000/api/posts`)
   
    return { props: { data } }
  }

 
 
function Index (props) {
   const {data} = props
   const allObj = data.posts.map((post)=><All key={post._id} post={post}/>)
   

    return (  
        <div>
            <Header>
                <link rel="stylesheet" type="text/css" href="/lib/bootstrap/css/bootstrap.min.css"/>
                <link href="/css/all.css" rel="stylesheet"/>
            </Header>
            <div className="container">
                <h1>All Posts</h1>
                
                <div class="table-responsive">
                    <table class="table table-bordered">
                        <thead>
                            <tr>
                            <th>Captions</th>
                            <th>link</th>
                            <th>Edit</th>
                            <th>delete</th>
                            </tr>
                        </thead>
                        <tbody>
                        {allObj}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    
    )
  }
export default Index
