export const categoryReturn =(data, category, num, type)=>{
    
    
    return data.reduce((acc, post)=>{
         if(acc.length === num) return acc;
         if(post[type] === category) acc.push(post);
         return acc;
 },[])  
 }

 export const filterCat =(data,attri1,attri2,attri3,val1, val2,val3)=>{
     if(!(data instanceof Array)) return null;
     return data.filter(value=>
         value[attri1] === val1 && value[attri2]===val2 &&value[attri3] === val3
     )
 }

