export async function getPosts(req, cat) {
    const posts = await req.db
    .collection('posts')
    .find({category:cat})
    .sort({createdAt:-1})
    .toArray();

    if (!posts) return null;
    const postArray = post.map(value => {
      const {image_link,caption,author,source,source_link,category,headings,abstract_cat,content} =value
      return {image_link,
        caption,
        author,
        source,
        source_link,
        category,
        headings,
        abstract_cat,
      content}
    });

    return {postsArray}
  }
