export default function(middleware){
    return (req, res)=> new Promise((resolve, reject)=>{
        middleware(req, res, (result)=>{
            if(result instanceof Error){
                return res.send("unauthorized")
            }
            else{
                return res.send("authorized")
            }
        })
    })
}
